import { Component, EventEmitter, Input, Output } from '@angular/core';
import { TaskItem } from 'src/app/models/taskItem';
import axios from 'axios';
import { DataService } from 'src/app/rxjs/DataService';

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.scss']
})
export class FormComponent {
  @Output() onFormSubmitted = new EventEmitter<TaskItem>()
  @Input() getTasks!: () => void;

  constructor(private dataService: DataService) {}

  _id = ""
  taskName=""
  __v = 0

  payload = {}

  submit(event: Event) {
    event.preventDefault();
    // const task: TaskItem = {
    //   _id: this._id,
    //   taskName: this.taskName,
    //   completed: false,
    //   __v: this.__v
    // }
    this.payload = {
      taskName: this.taskName,
      completed: false,
    }
    axios({
      url: 'http://localhost:8080/tasks/save',
      method: 'POST',
      data: this.payload,
    })
    .then(() => {
      console.log('Submitted')
      this.getTasks()
    })
    .catch(() => {
      console.log("Failed to Submit")
    })
    // this.onFormSubmitted.emit(task);
    this.taskName=""
  }
}
<div>
    <form class="add-task" (submit)="submit($event)">
        <p>Task: 
            <input type="text" [(ngModel)]="taskName" [ngModelOptions]="{standalone: true}" />
        </p>
        <button>Submit</button>
    </form>
</div>
