import { Component, Input } from '@angular/core';
import { TaskItem } from 'src/app/models/taskItem';
import axios from 'axios';

@Component({
  selector: 'app-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.scss']
})
export class ListComponent {
  @Input() tasks: TaskItem[];

  constructor() {
    this.tasks = []
  }

  completed: boolean = false;

  toggleComplete(_id: string) {
    const task = this.tasks.find(item => item._id === _id)
    if (task) {
      this.completed === !task.completed;
      console.log(_id)
      axios.put(`http://localhost:8080/tasks/${_id}`, {completed: !task.completed})
      .then(response => {
        console.log('Task completed status updated:', response.data);
      })
      .catch(error => {
        console.error('Error updating task completed status:', error);
      });
    }
  }

  delete(_id: string) {
    const task = this.tasks.find(item => item._id === _id);
    if (task) {
    axios.delete(`http://localhost:8080/tasks/${_id}`)
    .then(() => {
      console.log('Successfully Delete');
    })
    .catch(error => {
      console.error('Error updating task completed status:', error);
    });
    }

  }

}
<ul class="task-list">
    <li class="task" *ngFor="let item of tasks">
        <input class="complete" type="checkbox" [defaultChecked]="item.completed" (change) = "toggleComplete(item._id)" />{{item.taskName}}
        <button (click) = "delete(item._id)">Delete</button>
    </li>
</ul>
